function drawSentimentChart(pos, neg) {
            var data = [{
                values: [pos, neg],
                labels: ['Positive', 'Negative'],
                type: 'pie',
                marker: {
                    colors: ['#28a745', '#dc3545']
                },
                textinfo: 'label+percent',
                textposition: 'outside',
                automargin: true
            }];

            var layout = {
                title: 'Sentiment Analysis',
                height: 400,
                width: 600,
                showlegend: false
            };

            Plotly.newPlot('sentimentChart', data, layout);
        }